import {shuffleAnswers} from "./utils"
export enum Difficulty {
  EASY = "easy",
  MEDIUM = "medium",
  HARD = "hard",
}

export type Question = {
  category: string;
  correct_answer: string;
  difficulty: string;
  incorrect_answer: string[];
  question: string;
  type: string;
};

export type QuestionState = Question & { answers: string[] };

export const getTriviaQuestions = async (
  amount: number,
  difficulty: Difficulty
) => {
  const reqUrl = `https://opentdb.com/api.php?amount=${amount}&difficulty=${difficulty}&type=multiple`;
  const data = await (await fetch(reqUrl)).json();
    return data.results.map((question: Question) => (
        {
            ...question,
            answer: shuffleAnswers
      }
  ))
};
